import React, { useEffect, useState } from 'react';
import { Donut } from 'britecharts-react';


const DonutChart = ({users}) => {
  const [user, setUser] = useState([]);
  const [friendCount, setFriendCount] = useState([]);

  useEffect(() => {
  }, [])

  const graphClicked = () => {
    console.log("clicked")
  }

  const handleMouseOver = (abe) => {
    console.log('Mouse Over: ', abe.data.name);
    let filtered = users.filter(user => user.name === abe.data.name)
    setUser(filtered[0]);
    setUser(filtered[0].setFriendCount.length);
    // let filtered: users.filter(user => user.name === abe.data.name)

    console.log("user set: ", user.friends_with.length)
    console.log("user let: ", filtered[0].name)
  }

  return (

    <div className="donut">
      <span>{user.name}</span>
      <span>{user.friends_with}</span>
      <Donut
        data={users}
        externalRadius={100}
        internalRadius={47}
        highlightSliceById={1}
        customMouseOver={handleMouseOver.bind(this)}
        />
    </div>
  )
}

export default DonutChart;
