import styled from "styled-components";
// import { baseTheme } from "../../theme";

const CheckBox = styled.div`
  border-radius: 3px;
  display: flex;
  vertical-align: top;

  input {
    display: none;
  }

  input + span {
    border-radius: inherit;
    border: 1px solid #aaa;
    box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.3);
    cursor: pointer;
    display: block;
    flex-shrink: 0;
    height: 17px;
    margin-right: 8px;
    margin-top: 3px;
    position: relative;
    width: 17px;

    &:after {
      color: "red";
      content: "✔";
      font-size: 16px;
      left: 0;
      line-height: 17px;
      position: absolute;
      top: 0;
      transition: all 0.2s;
    }
  }

  input:not(:checked) + span {
    &:after {
      opacity: 0;
      transform: scale(0);
    }
  }
  input:disabled:not(:checked) + span {
    &:before {
      background-color: #ddd;
      border-color: #bbb;
      box-shadow: none;
    }
  }
  input:checked + span {
    &:after {
      opacity: 1;
      transform: scale(1);
    }
  }
  input:disabled:checked + span {
    &:after {
      color: #b2b2b2;
    }
  }
  input:disabled + span {
    color: #b2b2b2;
  }
  input:checked:focus + span,
  input:not(:checked):focus + span {
    &:before {
      border: 1px dotted blue;
    }
  }
`;
CheckBox.defaultProps = {
  theme: baseTheme
};

export const Label = styled.label`
  color: #454545;
  font-size: 14px;
  line-height: 1.29;

  a {
    font-weight: 500;
    color: #727272;
  }
`;

export default CheckBox;
